<?phpclass Plugins_CompanySessionValidator{		private $exemptedProperties = array();		public function preDispatch(LMVC_Request $request)	{		$this->exemptedProperties['login'] = array('index');		$this->exemptedProperties['logout'] = array('index','success');		$this->exemptedProperties['activate'] = array('index','billing');		$this->exemptedProperties['payment'] = array('activate','success','cancel');		$this->exemptedProperties['register'] = array('index','success','leadcategories','type','signupcomplete');				$this->exemptedProperties['resetpassword'] = array('index','reset','success');				if($request->getModuleName() == "company")		{			$this->setVars();						$currentController = $request->getControllerName();			$currentAction = $request->getActionName();						if(array_key_exists($currentController,$this->exemptedProperties))			{								if(in_array($currentAction,$this->exemptedProperties[$currentController]))				{					return;				}				else{										$this->validateSession();									}							}			else			{				$this->validateSession();			}								}	}		private function validateSession()	{		$id = LMVC_Session::get('companyId');		if(empty($id))		{						$this->setVars();			$retUrl = $_SERVER['REQUEST_URI'];								header("Location: /company/login?retURL=$retUrl");			exit();			}					}		private function setVars()	{		$id = LMVC_Session::get('companyId');				if(empty($id)) $id=0;		LMVC_Front::getInstance()->setPreDispatchVar('session_company_id',$id);	}		}?>